args.new_string:
/**
 * 为易财务分析 - 全局样式
 * 基于 docs/ped.md UI 设计规范
 */

/* ========== 全局 CSS 变量 ========== */
page {
  /* 主色 */
  --primary-color: #1890FF;
  --primary-color-light: #40A9FF;
  --primary-color-dark: #0052D9;

  /* 辅助色（国内涨跌习惯） */
  --color-rise: #F5222D;       /* 涨/优 - 红色 */
  --color-fall: #52C41A;       /* 跌/劣 - 绿色 */
  --color-warning: #FA8C16;    /* 警告 - 橙色 */

  /* 中性色 */
  --color-bg-page: #F5F5F5;    /* 页面背景 */
  --color-bg-card: #FFFFFF;    /* 卡片背景 */
  --color-text-primary: #1F1F1F;    /* 标题 */
  --color-text-secondary: #434343;  /* 正文 */
  --color-text-tertiary: #8C8C8C;   /* 辅助说明 */
  --color-border: #F0F0F0;     /* 边框/分割线 */

  /* 间距 */
  --spacing-xs: 8rpx;
  --spacing-sm: 16rpx;
  --spacing-md: 24rpx;
  --spacing-lg: 32rpx;
  --spacing-xl: 48rpx;

  /* 圆角 */
  --radius-sm: 8rpx;
  --radius-md: 12rpx;
  --radius-lg: 16rpx;

  /* 阴影 */
  --shadow-card: 0 2rpx 16rpx rgba(0, 0, 0, 0.05);
  --shadow-hover: 0 4rpx 24rpx rgba(0, 0, 0, 0.08);

  /* 字号 */
  --font-size-xs: 22rpx;
  --font-size-sm: 26rpx;
  --font-size-md: 28rpx;
  --font-size-lg: 32rpx;
  --font-size-xl: 36rpx;
  --font-size-xxl: 48rpx;
  --font-size-number: 56rpx;   /* 关键数字 */

  /* 页面基础配置 */
  background-color: var(--color-bg-page);
  color: var(--color-text-secondary);
  font-size: var(--font-size-md);
  line-height: 1.6;
}

/* ========== 全局重置 ========== */
* {
  box-sizing: border-box;
}

/* ========== 通用布局类 ========== */
.page {
  min-height: 100vh;
  background-color: var(--color-bg-page);
  padding: var(--spacing-lg);
}

.container {
  width: 100%;
  padding: var(--spacing-lg);
}

/* ========== 卡片样式 ========== */
.card {
  background-color: var(--color-bg-card);
  border-radius: var(--radius-md);
  box-shadow: var(--shadow-card);
  padding: var(--spacing-lg);
  margin-bottom: var(--spacing-md);
}

.card:active {
  box-shadow: var(--shadow-hover);
  opacity: 0.95;
}

.card-title {
  font-size: var(--font-size-lg);
  font-weight: 600;
  color: var(--color-text-primary);
  margin-bottom: var(--spacing-md);
}

.card-content {
  font-size: var(--font-size-md);
  color: var(--color-text-secondary);
}

/* ========== 按钮样式 ========== */
.btn {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 88rpx;
  border-radius: var(--radius-md);
  font-size: var(--font-size-md);
  font-weight: 500;
  transition: all 0.3s;
  border: none;
  outline: none;
}

.btn-primary {
  background-color: var(--primary-color);
  color: #FFFFFF;
}

.btn-primary:active {
  background-color: var(--primary-color-dark);
  opacity: 0.9;
}

.btn-primary.disabled {
  background-color: #D9D9D9;
  color: #FFFFFF;
  opacity: 0.6;
}

.btn-secondary {
  background-color: var(--color-bg-card);
  color: var(--primary-color);
  border: 2rpx solid var(--primary-color);
}

.btn-secondary:active {
  background-color: var(--primary-color);
  color: #FFFFFF;
}

/* ========== 文本样式 ========== */
.text-primary {
  color: var(--color-text-primary);
}

.text-secondary {
  color: var(--color-text-secondary);
}

.text-tertiary {
  color: var(--color-text-tertiary);
}

.text-rise {
  color: var(--color-rise);
}

.text-fall {
  color: var(--color-fall);
}

.text-lg {
  font-size: var(--font-size-lg);
}

.text-xl {
  font-size: var(--font-size-xl);
}

.text-number {
  font-size: var(--font-size-number);
  font-weight: 600;
  font-family: 'DIN Alternate', 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

/* ========== 布局工具类 ========== */
.flex {
  display: flex;
}

.flex-row {
  flex-direction: row;
}

.flex-column {
  flex-direction: column;
}

.flex-center {
  align-items: center;
  justify-content: center;
}

.flex-between {
  justify-content: space-between;
}

.flex-align-center {
  align-items: center;
}

.flex-1 {
  flex: 1;
}

/* ========== 间距工具类 ========== */
.mt-xs { margin-top: var(--spacing-xs); }
.mt-sm { margin-top: var(--spacing-sm); }
.mt-md { margin-top: var(--spacing-md); }
.mt-lg { margin-top: var(--spacing-lg); }
.mt-xl { margin-top: var(--spacing-xl); }

.mb-xs { margin-bottom: var(--spacing-xs); }
.mb-sm { margin-bottom: var(--spacing-sm); }
.mb-md { margin-bottom: var(--spacing-md); }
.mb-lg { margin-bottom: var(--spacing-lg); }
.mb-xl { margin-bottom: var(--spacing-xl); }

.pt-xs { padding-top: var(--spacing-xs); }
.pt-sm { padding-top: var(--spacing-sm); }
.pt-md { padding-top: var(--spacing-md); }
.pt-lg { padding-top: var(--spacing-lg); }

.pb-xs { padding-bottom: var(--spacing-xs); }
.pb-sm { padding-bottom: var(--spacing-sm); }
.pb-md { padding-bottom: var(--spacing-md); }
.pb-lg { padding-bottom: var(--spacing-lg); }

/* ========== 边框与分割线 ========== */
.border-top {
  border-top: 1rpx solid var(--color-border);
}

.border-bottom {
  border-bottom: 1rpx solid var(--color-border);
}

.divider {
  height: 1rpx;
  background-color: var(--color-border);
  margin: var(--spacing-md) 0;
}

