  data: {
    fileUploaded: false,
    fileName: '',
    analyzing: false,
    currentStep: 0,
    remainingTime: 30,
    failed: false,
    errorMessage: ''
  },

  handleChooseFile() {
    if (this.data.fileUploaded) return;

    // TODO: 实际实现时使用 wx.chooseMessageFile 选择文件
    // 当前使用 Mock 模拟上传
    wx.showToast({
      title: '文件选择中...',
      icon: 'loading',
      duration: 1000
    });

    setTimeout(() => {
      this.setData({
        fileUploaded: true,
        fileName: '格力电器_2024年报.xlsx'
      });
      wx.showToast({
        title: '上传成功',
        icon: 'success'
      });
    }, 1000);
  },

  handleReupload(e) {
    e.stopPropagation();
    this.setData({
      fileUploaded: false,
      fileName: ''
    });
  },

  handleDeleteFile(e) {
    e.stopPropagation();
    this.setData({
      fileUploaded: false,
      fileName: ''
    });
  },

  handleStartAnalyze() {
    if (!this.data.fileUploaded || this.data.analyzing) return;

    this.setData({
      analyzing: true,
      currentStep: 1,
      remainingTime: 30,
      failed: false
    });

    // Mock 分析流程（实际应调用云函数创建任务）
    this.simulateAnalysis();
  },

  simulateAnalysis() {
    const timer = setInterval(() => {
      const { currentStep, remainingTime } = this.data;

      if (remainingTime <= 0) {
        clearInterval(timer);
        // 分析完成，跳转到结果页
        wx.redirectTo({
          url: '/pages/result/index'
        });
        return;
      }

      // 每 7-8 秒进入下一步
      const newStep = currentStep < 4 ? (30 - remainingTime > currentStep * 7 ? currentStep + 1 : currentStep) : 4;

      this.setData({
        remainingTime: remainingTime - 1,
        currentStep: newStep
      });
    }, 1000);

    // 保存 timer 以便后续清理
    this.timer = timer;
  },

  handleRetry() {
    this.setData({
      failed: false,
      errorMessage: ''
    });
    this.handleStartAnalyze();
  },

  onUnload() {
    // 清理定时器
    if (this.timer) {
      clearInterval(this.timer);
    }
  }
})

