args.contents:
/**
 * 为易财务分析 - 全局样式
 * 现代极简风格，保留金融属性配色
 */

/* ========== 全局配置 ========== */
page {
  box-sizing: border-box;
  background-color: #FAFAFA;
  color: #2C3E50;
  font-size: 28rpx;
  line-height: 1.8;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'PingFang SC', 'Hiragino Sans GB', 'Microsoft YaHei', sans-serif;
}

/* ========== 通用布局类 ========== */
.page {
  width: 100%;
  min-height: 100vh;
  background-color: #FAFAFA;
  padding: 40rpx 32rpx;
  box-sizing: border-box;
}

/* ========== 卡片样式（大圆角、柔和阴影） ========== */
.card {
  background-color: #FFFFFF;
  border-radius: 32rpx;
  box-shadow: 0 4rpx 20rpx rgba(0, 0, 0, 0.04);
  padding: 40rpx 36rpx;
  margin-bottom: 32rpx;
  border: none;
}

.card:active {
  box-shadow: 0 8rpx 32rpx rgba(0, 0, 0, 0.08);
  transform: scale(0.98);
}

.card-title {
  font-size: 36rpx;
  font-weight: 600;
  color: #1A1A1A;
  margin-bottom: 32rpx;
  letter-spacing: -0.5rpx;
}

.card-content {
  font-size: 28rpx;
  color: #5A6C7D;
  line-height: 1.8;
}

/* ========== 按钮样式（大圆角、现代设计） ========== */
.btn {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 96rpx;
  border-radius: 48rpx;
  font-size: 32rpx;
  font-weight: 600;
  border: none;
  outline: none;
  width: 100%;
  letter-spacing: 1rpx;
  box-shadow: 0 4rpx 16rpx rgba(24, 144, 255, 0.2);
}

.btn-primary {
  background: linear-gradient(135deg, #1890FF 0%, #0052D9 100%);
  color: #FFFFFF;
}

.btn-primary:active {
  background: linear-gradient(135deg, #0052D9 0%, #003A9E 100%);
  box-shadow: 0 2rpx 8rpx rgba(24, 144, 255, 0.3);
}

.btn-primary[disabled] {
  background: #E5E5E5;
  color: #999999;
  box-shadow: none;
  opacity: 0.6;
}

.btn-secondary {
  background-color: #FFFFFF;
  color: #1890FF;
  border: 2rpx solid #1890FF;
  box-shadow: 0 2rpx 8rpx rgba(24, 144, 255, 0.1);
}

.btn-secondary:active {
  background-color: #F0F7FF;
  border-color: #0052D9;
}

/* ========== 文本样式 ========== */
.text-primary {
  color: #1A1A1A;
}

.text-secondary {
  color: #5A6C7D;
}

.text-tertiary {
  color: #8C9AAF;
}

.text-rise {
  color: #F5222D;
}

.text-fall {
  color: #52C41A;
}

.text-gold {
  color: #D4AF37;
}

.text-lg {
  font-size: 36rpx;
}

.text-xl {
  font-size: 42rpx;
}

.text-xxl {
  font-size: 56rpx;
}

.text-number {
  font-size: 64rpx;
  font-weight: 700;
  font-family: 'DIN Alternate', 'Helvetica Neue', Helvetica, Arial, sans-serif;
  letter-spacing: -1rpx;
}

/* ========== 布局工具类 ========== */
.flex {
  display: flex;
}

.flex-row {
  flex-direction: row;
}

.flex-column {
  flex-direction: column;
}

.flex-center {
  align-items: center;
  justify-content: center;
}

.flex-between {
  justify-content: space-between;
}

.flex-align-center {
  align-items: center;
}

.flex-1 {
  flex: 1;
}

/* ========== 间距工具类（更大的留白） ========== */
.mt-xs { margin-top: 12rpx; }
.mt-sm { margin-top: 20rpx; }
.mt-md { margin-top: 32rpx; }
.mt-lg { margin-top: 48rpx; }
.mt-xl { margin-top: 64rpx; }

.mb-xs { margin-bottom: 12rpx; }
.mb-sm { margin-bottom: 20rpx; }
.mb-md { margin-bottom: 32rpx; }
.mb-lg { margin-bottom: 48rpx; }
.mb-xl { margin-bottom: 64rpx; }

.pt-xs { padding-top: 12rpx; }
.pt-sm { padding-top: 20rpx; }
.pt-md { padding-top: 32rpx; }
.pt-lg { padding-top: 48rpx; }

.pb-xs { padding-bottom: 12rpx; }
.pb-sm { padding-bottom: 20rpx; }
.pb-md { padding-bottom: 32rpx; }
.pb-lg { padding-bottom: 48rpx; }

/* ========== 边框与分割线 ========== */
.border-top {
  border-top: 1rpx solid #F0F2F5;
}

.border-bottom {
  border-bottom: 1rpx solid #F0F2F5;
}

.divider {
  height: 1rpx;
